module.exports = Route;

function Callback(){
	this.literal = arguments[0];
	
	// Route callback prefix, routes.index -> routes
	this.prefix = this.literal.split('.')[0];
	// clear prefix if there basically is none
	if (this.prefix === this.literal) {
		delete this.prefix;
	} else {
		// create suffix
		this.suffix = this.literal.split('.')[1];
	};
}

function Route(obj) {
	// object literal of a route from the parser
	this.method = obj.method;
	this.path = obj.path;
	this.callbacks = [];
	for (var i = 0; i < obj.callbacks.length; i++) {
		this.callbacks[i] = new Callback(obj.callbacks[i]);
	};
}
